import React, { useState } from 'react';
import { Button, Form, Input, Image, Row, Col } from 'antd';
const App = () => {
  const [img, setImg] = useState('');
  const [link, setLink] = useState('');
  const [name, setName] = useState('');
  const [intro, setIntro] = useState('');
  return (
    <Row justify='space-between'>
      <Col span={6} order={1}>
        <Image
          height={240}
          src={img}
          fallback={
            'data:image/png;base64,'
          }
        />
      </Col>
      <Col span={16} order={2}>
        <Form>
          <Form.Item label='海报：'>
            <Input
              name='img'
              onChange={(e) => setImg(e.target.value)}
              placeholder='电影海报'
            />
          </Form.Item>
          <Form.Item label='链接'>
            <Input
              name='link'
              onChange={(e) => setLink(e.target.value)}
              placeholder='下载链接'
              value={link}
            />
          </Form.Item>
          <Form.Item label='名称：'>
            <Input
              name='name'
              onChange={(e) => setName(e.target.value)}
              placeholder='电影名称'
              value={name}
            />
          </Form.Item>
          <Form.Item label='简介：'>
            <Input
              name='intro'
              onChange={(e) => setIntro(e.target.value)}
              placeholder='影片简介'
              value={intro}
            />
          </Form.Item>
          <Form.Item>
            <Button
              onClick={() => console.log(img, link, name, intro)}
              type='primary'
            >
              上传
            </Button>
          </Form.Item>
        </Form>
      </Col>
    </Row>
  );
};
export default App;
